import { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import SwapWidget from './components/SwapWidget';
import TransactionsPage from './components/TransactionsPage';
import VaultsPage from './components/VaultsPage';
import WalletModal from './components/WalletModal';
import ChatWidget from './components/ChatWidget';
import { TRENDING } from './data/constants';

export default function App() {
  const [theme, setTheme] = useState('dark');
  const [page, setPage] = useState('swap');
  const [connectedWallet, setConnectedWallet] = useState(null);
  const [showWalletModal, setShowWalletModal] = useState(false);
  const [tempWallet, setTempWallet] = useState(null);

  useEffect(() => {
    document.body.setAttribute('data-theme', theme);
  }, [theme]);

  function handleWalletConnect() {
    if (connectedWallet) setConnectedWallet(null);
    else setShowWalletModal(true);
  }

  function handleWalletContinue() {
    if (tempWallet) {
      setConnectedWallet(tempWallet);
      setShowWalletModal(false);
      setTempWallet(null);
    }
  }

  const subTitle =
    page === 'swap'   ? 'Relay Swap' :
    page === 'vaults' ? 'Relay Vaults' :
                        'Relay Transactions';

  return (
    <>
      {/* BACKGROUND */}
      <div className="grid-bg" />
      <div className="orb orb1" />
      <div className="orb orb2" />
      <div className="orb orb3" />

      {/* NAVBAR */}
      <Navbar
        page={page}
        setPage={setPage}
        theme={theme}
        setTheme={setTheme}
        connectedWallet={connectedWallet}
        onConnectClick={handleWalletConnect}
      />

      {/* SUB-HEADER */}
      <div className="sub-header">
        <div className="sub-title">
          <div className="sub-title-dot" />
          Â· {subTitle}
        </div>
        <div className="sub-stats">
          <div className="sub-stat">VOL 24H <span>$2.4B</span></div>
          <div className="sub-stat">TVL <span>$18.7B</span></div>
          <div className="sub-stat">GAS <span>12 GWEI</span></div>
          <div className="sub-stat">BLOCK <span>#21,847,293</span></div>
        </div>
      </div>

      {/* SWAP PAGE */}
      {page === 'swap' && (
        <div className="page-centered">
          <div className="trending-bar">
            {/* <div className="trending-chip label">ðŸ”¥ Trending</div>
            {TRENDING.map(t => (
              <div key={t.sym} className="trending-chip">
                <div style={{ width: 14, height: 14, borderRadius: '50%', background: t.color + '33', display: 'flex', alignItems: 'center', justifyContent: 'center', fontSize: 8 }}>
                  {t.icon}
                </div>
                {t.sym}
              </div>
            ))} */}
            <div className="trending-chip" style={{ padding: '5px 8px' }}>â€º</div>
          </div>
          <SwapWidget connectedWallet={connectedWallet} onConnectClick={() => setShowWalletModal(true)} />
        </div>
      )}

      {page === 'vaults' && <VaultsPage />}
      {page === 'txs' && <TransactionsPage />}

      {/* WALLET MODAL */}
      {showWalletModal && (
        <WalletModal
          onClose={() => { setShowWalletModal(false); setTempWallet(null); }}
          onSelect={setTempWallet}
          selectedWallet={tempWallet}
          showContinue
          onContinue={handleWalletContinue}
        />
      )}

      <ChatWidget />
    </>
  );
}
