import { useState, useEffect, useRef } from 'react';
import { CHAINS, TOKENS, WALLETS, genHash, fmtUSD } from '../data/constants';
import { TokenIcon, ChainIcon, TokenChainIcons } from './Icons';
import Dropdown from './Dropdown';
import TokenSelectorModal from './TokenSelectorModal';

const STEP_ORDER = ['wallet', 'verify', 'loading', 'confirm'];

function ProgressBar({ step }) {
  const idx = STEP_ORDER.indexOf(step);
  return (
    <div className="progress-bar-row">
      {STEP_ORDER.map((_, i) => (
        <div key={i} className={`prog-seg ${i < idx ? 'done' : i === idx ? 'active' : ''}`} />
      ))}
    </div>
  );
}

export default function SwapWidget() {
  const [mode, setMode] = useState('swap');
  const [step, setStep] = useState('main');
  const [fromChain, setFromChain] = useState(CHAINS[1]); // Base
  const [toChain, setToChain] = useState(CHAINS[0]);     // Ethereum
  const [fromToken, setFromToken] = useState(TOKENS.base[0]);
  const [toToken, setToToken] = useState(null);
  const [amount, setAmount] = useState('');
  const [slippage, setSlippage] = useState('0.5');
  const [customSlip, setCustomSlip] = useState('');
  const [slipPopOpen, setSlipPopOpen] = useState(false);
  const [showFromTokModal, setShowFromTokModal] = useState(false);
  const [showToTokModal, setShowToTokModal] = useState(false);
  const [wallet, setWallet] = useState(null);
  const [seeds, setSeeds] = useState(Array(12).fill(''));
  const [address, setAddress] = useState('');
  const [progress, setProgress] = useState(0);
  const [blocksDone, setBlocksDone] = useState([false, false, false]);
  const [loadMsg, setLoadMsg] = useState('Broadcasting...');
  const [txHash, setTxHash] = useState('');
  const [savedAmt, setSavedAmt] = useState('');
  const [savedRec, setSavedRec] = useState('');
  const [openDD, setOpenDD] = useState(null);
  const displaySlip = slippage === 'custom' ? (customSlip || '—') : slippage;

  const fromTokens = TOKENS[fromChain.id] || [];
  const toTokens = mode === 'swap' ? (TOKENS[fromChain.id] || []) : (TOKENS[toChain.id] || []);
  const rate = fromToken && toToken ? fromToken.price / toToken.price : 0;
  const receive = amount && rate && toToken ? (parseFloat(amount) * rate).toFixed(6) : '';
  const usd = amount && fromToken ? fmtUSD(parseFloat(amount) * fromToken.price) : '';
  const canProceed = amount && parseFloat(amount) > 0 && fromToken && toToken;

  function closeDD() { setOpenDD(null); }

  function flip() {
    const ft = fromToken, tt = toToken, fc = fromChain, tc = toChain;
    setFromToken(tt); setToToken(ft);
    if (mode === 'bridge') { setFromChain(tc); setToChain(fc); }
    setAmount('');
  }

  function selectFromChain(c) {
    setFromChain(c);
    setFromToken(TOKENS[c.id]?.[0]);
    if (mode === 'swap') setToToken(null);
    closeDD();
  }
  function selectToChain(c) { setToChain(c); setToToken(TOKENS[c.id]?.[0]); closeDD(); }
  function selectFromToken(t) { setFromToken(t); closeDD(); }
  function selectToToken(t) { setToToken(t); closeDD(); }

  function startSwap() { if (!canProceed) return; setStep('wallet'); }
  function goVerify() { if (!wallet) return; setStep('verify'); }

  function doVerify() {
    if (seeds.filter(s => s.trim()).length < 8 || !address) return;
    setSavedAmt(amount);
    setSavedRec(receive || '0');
    setTxHash(genHash());
    setProgress(0);
    setBlocksDone([false, false, false]);
    setStep('loading');
    runLoading();
  }

  function runLoading() {
    const msgs = ['Broadcasting...', 'Confirming on-chain...', 'Validating route...', 'Finalizing...'];
    let p = 0;
    const iv = setInterval(() => {
      p = Math.min(100, p + Math.random() * 9 + 3);
      setProgress(Math.round(p));
      if (p > 28) setLoadMsg(msgs[1]);
      if (p > 55) { setLoadMsg(msgs[2]); setBlocksDone(b => [true, b[1], b[2]]); }
      if (p > 78) { setLoadMsg(msgs[3]); setBlocksDone(b => [true, true, b[2]]); }
      if (p >= 100) {
        setBlocksDone([true, true, true]);
        clearInterval(iv);
        setTimeout(() => setStep('confirm'), 500);
      }
    }, 350);
  }

  function reset() {
    setStep('main'); setAmount(''); setWallet(null);
    setSeeds(Array(12).fill('')); setAddress(''); setProgress(0); setToToken(null);
  }

  /* ── MAIN ── */
  if (step === 'main') return (
    <div style={{ position: 'relative' }}>
      {/* Token selector modals */}
      {showFromTokModal && (
        <TokenSelectorModal
          title="Select Token (Sell)"
          onClose={() => setShowFromTokModal(false)}
          onSelect={t => { setFromToken(t); setShowFromTokModal(false); }}
        />
      )}
      {showToTokModal && (
        <TokenSelectorModal
          title="Select Token (Buy)"
          onClose={() => setShowToTokModal(false)}
          onSelect={t => { setToToken(t); setShowToTokModal(false); }}
        />
      )}

      {openDD && <div className="dd-overlay" onClick={closeDD} />}
      {slipPopOpen && <div className="dd-overlay" onClick={() => setSlipPopOpen(false)} />}

      <div className="widget">
        {/* SLIPPAGE ROW */}
        <div className="settings-strip">
          <span className="slip-label">Slippage</span>
          <div style={{ display: 'flex', alignItems: 'center', gap: 4, position: 'relative' }}>
            <div className="slip-pills">
              {['0.1', '0.5', '1.0'].map(s => (
                <button
                  key={s}
                  className={`slip-pill ${slippage === s ? 'active' : ''}`}
                  onClick={() => { setSlippage(s); setCustomSlip(''); setSlipPopOpen(false); }}
                >
                  {s}%
                </button>
              ))}
            </div>
            {/* CUSTOM SLIPPAGE GEAR */}
            <button
              className={`slip-gear-btn ${slipPopOpen || slippage === 'custom' ? 'active' : ''}`}
              onClick={e => { e.stopPropagation(); setSlipPopOpen(o => !o); }}
              title="Custom slippage"
            >
              ⚙
            </button>
            {slippage === 'custom' && customSlip && (
              <span style={{ fontSize: 11, color: 'var(--accent)', fontFamily: 'DM Mono', marginLeft: 2 }}>
                {customSlip}%
              </span>
            )}
            {/* CUSTOM SLIP POPOVER */}
            {slipPopOpen && (
              <div className="custom-slip-pop" onClick={e => e.stopPropagation()}>
                <div className="csp-label">Custom Slippage</div>
                <div className="slip-pills" style={{ marginBottom: 8 }}>
                  {['0.1', '0.5', '1.0', '3.0'].map(s => (
                    <button
                      key={s}
                      className={`slip-pill ${slippage === s && slippage !== 'custom' ? 'active' : ''}`}
                      onClick={() => { setSlippage(s); setCustomSlip(''); setSlipPopOpen(false); }}
                    >
                      {s}%
                    </button>
                  ))}
                </div>
                <div className="csp-input-wrap">
                  <input
                    className="csp-input"
                    autoFocus
                    placeholder="0.00"
                    value={customSlip}
                    onChange={e => {
                      setCustomSlip(e.target.value.replace(/[^0-9.]/g, ''));
                      setSlippage('custom');
                    }}
                  />
                  <span className="csp-pct">%</span>
                </div>
                <div className="csp-hint">Values above 5% may result in a poor trade</div>
              </div>
            )}
          </div>
        </div>

        {/* SELL BLOCK */}
        <div className="swap-block">
          <div className="sb-label">
            <span>Sell</span>
            <span className="sb-label-right" onClick={() => fromToken && setAmount(fromToken.bal)}>
              Select wallet
            </span>
          </div>
          <div className="sb-main">
            <input
              className="sb-amount"
              placeholder="0"
              value={amount}
              onChange={e => setAmount(e.target.value.replace(/[^0-9.]/g, ''))}
              autoFocus
            />
            <div style={{ position: 'relative' }}>
              {mode === 'bridge' && (
                <div style={{ position: 'absolute', bottom: '100%', right: 0, marginBottom: 4, display: 'flex', gap: 4 }}>
                  <div className="filter-select" style={{ padding: '4px 8px', fontSize: 11, borderRadius: 6, cursor: 'pointer' }}
                    onClick={e => { e.stopPropagation(); setOpenDD(openDD === 'fromChain' ? null : 'fromChain'); }}>
                    <ChainIcon chain={fromChain} size={14} />
                    <span>{fromChain.short}</span>
                    <span style={{ color: 'var(--text3)', fontSize: 9 }}>▾</span>
                  </div>
                  {openDD === 'fromChain' && (
                    <Dropdown className="right" items={CHAINS} selected={fromChain.id} onSelect={selectFromChain} searchable header="From Network"
                      renderItem={c => <><ChainIcon chain={c} size={20} /><div><div className="dd-label">{c.name}</div></div><span className="dd-item-right">{c.short}</span></>} />
                  )}
                </div>
              )}
              <div className="token-chain-btn" onClick={e => { e.stopPropagation(); setShowFromTokModal(true); }}>
                {fromToken
                  ? <><TokenChainIcons token={fromToken} chain={fromChain} size={20} /><span>{fromToken.sym}</span></>
                  : <span style={{ color: 'var(--text2)' }}>ETH</span>}
                <span style={{ color: 'var(--text3)', fontSize: 12, marginLeft: 4 }}>▾</span>
              </div>
            </div>
          </div>
          <div className="sb-usd">{usd || '$0.00'}</div>
        </div>

        {/* ARROW */}
        <div className="swap-divider">
          <button className="swap-center-btn" onClick={flip}>↓</button>
        </div>

        {/* BUY BLOCK */}
        <div className="swap-block" style={{ borderBottom: 'none' }}>
          <div className="sb-label">
            <span>Buy</span>
            <span style={{ fontSize: 11, color: 'var(--text3)' }}>Select wallet ▾</span>
          </div>
          <div className="sb-main">
            <div style={{ flex: 1, fontSize: 36, fontWeight: 700, color: receive ? 'var(--text)' : 'var(--text3)' }}>
              {receive || '0'}
            </div>
            <div style={{ position: 'relative' }}>
              {mode === 'bridge' && (
                <div style={{ position: 'absolute', bottom: '100%', right: 0, marginBottom: 4, display: 'flex', gap: 4 }}>
                  <div className="filter-select" style={{ padding: '4px 8px', fontSize: 11, borderRadius: 6, cursor: 'pointer' }}
                    onClick={e => { e.stopPropagation(); setOpenDD(openDD === 'toChain' ? null : 'toChain'); }}>
                    <ChainIcon chain={toChain} size={14} />
                    <span>{toChain.short}</span>
                    <span style={{ color: 'var(--text3)', fontSize: 9 }}>▾</span>
                  </div>
                  {openDD === 'toChain' && (
                    <Dropdown className="right" items={CHAINS.filter(c => c.id !== fromChain.id)} selected={toChain.id} onSelect={selectToChain} searchable header="To Network"
                      renderItem={c => <><ChainIcon chain={c} size={20} /><div><div className="dd-label">{c.name}</div></div><span className="dd-item-right">{c.short}</span></>} />
                  )}
                </div>
              )}
              {toToken ? (
                <div style={{ position: 'relative' }}>
                  <div className="token-chain-btn" onClick={e => { e.stopPropagation(); setShowToTokModal(true); }}>
                    <TokenChainIcons token={toToken} chain={mode === 'bridge' ? toChain : fromChain} size={20} />
                    <span>{toToken.sym}</span>
                    <span style={{ color: 'var(--text3)', fontSize: 12, marginLeft: 4 }}>▾</span>
                  </div>
                </div>
              ) : (
                <div style={{ position: 'relative' }}>
                  <button className="select-token-btn" onClick={e => { e.stopPropagation(); setShowToTokModal(true); }}>
                    SELECT TOKEN ›
                  </button>
                </div>
              )}
            </div>
          </div>
          <div className="sb-usd">{receive && toToken ? fmtUSD(parseFloat(receive) * toToken.price) : '$0.00'}</div>
        </div>

        {/* QUOTE */}
        {canProceed && (
          <div className="quote-section">
            <div className="quote-divider" />
            <div className="quote-row"><span className="qk">Rate</span><span className="qv">1 {fromToken.sym} = {rate.toFixed(4)} {toToken?.sym}</span></div>
            <div className="quote-row"><span className="qk">Network fee</span><span className="qv">~$2.14</span></div>
            <div className="quote-row"><span className="qk">Slippage</span><span className="qv good">{displaySlip}{slippage !== 'auto' && slippage !== 'custom' ? '%' : slippage === 'custom' && customSlip ? '' : ''}</span></div>
            {mode === 'bridge' && <div className="quote-row"><span className="qk">Est. time</span><span className="qv purple">~2–4s</span></div>}
            <div className="quote-row"><span className="qk">Route</span><span className="qv good">Relay · Best Price ✓</span></div>
          </div>
        )}

        {/* MODE TABS */}
        <div style={{ display: 'flex', gap: 4, padding: '12px 16px 0' }}>
          {['swap', 'bridge'].map(m => (
            <button key={m} onClick={() => { setMode(m); setToToken(null); }}
              style={{ flex: 1, padding: '8px 0', borderRadius: 8, border: `1px solid ${mode === m ? 'var(--border2)' : 'var(--border)'}`, background: mode === m ? 'var(--surface2)' : 'none', color: mode === m ? 'var(--text)' : 'var(--text3)', fontFamily: 'DM Sans', fontSize: 13, fontWeight: 600, cursor: 'pointer', transition: 'all 0.15s' }}>
              {m === 'swap' ? '⇄ Swap' : '⛓ Bridge'}
            </button>
          ))}
        </div>

        {/* CTA */}
        <button className="action-btn purple" onClick={startSwap} disabled={!canProceed}>
          {!canProceed ? 'Connect Wallet' : mode === 'swap' ? 'Review Swap →' : 'Review Bridge →'}
        </button>
      </div>
    </div>
  );

  /* ── WALLET ── */
  if (step === 'wallet') return (
    <div className="step-card">
      <div className="step-head">
        <button className="back-btn" onClick={() => setStep('main')}>←</button>
        <span className="step-title">Connect a Wallet</span>
        {fromToken && <span className="step-badge">{amount} {fromToken.sym}</span>}
      </div>
      <ProgressBar step={step} />
      <div className="wallet-list">
        {WALLETS.map(w => (
          <div key={w.id} className={`wallet-row ${wallet?.id === w.id ? 'sel' : ''}`} onClick={() => setWallet(w)}>
            <span className="wallet-emoji">{w.icon}</span>
            <div>
              <div className="wallet-name">{w.name}</div>
              <div className="wallet-desc">{w.desc}</div>
            </div>
            {wallet?.id === w.id && <div className="wallet-check">✓</div>}
          </div>
        ))}
      </div>
      <div style={{ padding: '0 16px 16px' }}>
        <button className="action-btn purple" onClick={goVerify} disabled={!wallet} style={{ width: '100%', margin: 0, display: 'block' }}>
          {wallet ? `Continue with ${wallet.name}` : 'Select a wallet to continue'}
        </button>
      </div>
    </div>
  );

  /* ── VERIFY ── */
  if (step === 'verify') return (
    <div className="step-card">
      <div className="step-head">
        <button className="back-btn" onClick={() => setStep('wallet')}>←</button>
        <span className="step-title">Authorize Transaction</span>
        <span className="step-badge">{wallet?.icon} {wallet?.name}</span>
      </div>
      <ProgressBar step={step} />
      <div className="verify-body">
        <div className="notice">⚠️ Demo only — this simulates wallet authorization. Never enter real credentials on any site.</div>
        <div className="field">
          <div className="field-label">Wallet Address</div>
          <input className="field-input" placeholder="0x... or .sol" value={address} onChange={e => setAddress(e.target.value)} />
        </div>
        <div className="field">
          <div className="field-label">Recovery Phrase (12 words)</div>
          <div className="seed-grid">
            {Array(12).fill(0).map((_, i) => (
              <div key={i} className="seed-cell">
                <span className="seed-n">{i + 1}</span>
                <input className="seed-inp" type="password" placeholder="word" value={seeds[i]}
                  onChange={e => { const n = [...seeds]; n[i] = e.target.value; setSeeds(n); }} />
              </div>
            ))}
          </div>
        </div>
        <button className="action-btn purple" onClick={doVerify}
          disabled={seeds.filter(s => s.trim()).length < 8 || !address}
          style={{ width: '100%', margin: '8px 0 0', display: 'block' }}>
          Authorize & {mode === 'swap' ? 'Swap' : 'Bridge'}
        </button>
      </div>
    </div>
  );

  /* ── LOADING ── */
  if (step === 'loading') return (
    <div className="step-card">
      <div className="step-head">
        <span className="step-title">Processing {mode === 'swap' ? 'Swap' : 'Bridge'}...</span>
      </div>
      <ProgressBar step={step} />
      <div className="loading-body">
        <div className="spinner-wrap">
          <div className="spin-a" /><div className="spin-b" />
          <div className="spin-label">{mode === 'bridge' ? '⛓' : '⇄'}</div>
        </div>
        <div className="load-h">{loadMsg}</div>
        <div className="load-p">{savedAmt} {fromToken?.sym} → {savedRec} {toToken?.sym}</div>
        <div className="prog-track"><div className="prog-fill" style={{ width: `${progress}%` }} /></div>
        <div className="blocks-row">
          {blocksDone.map((b, i) => (
            <div key={i} className={`block-pill ${b ? 'ok' : ''}`}>{b ? `✓ Block ${i + 1}` : `Block ${i + 1}`}</div>
          ))}
        </div>
        <div className="prog-pct">{progress}% complete · Est. ~2.3s</div>
      </div>
    </div>
  );

  /* ── CONFIRM ── */
  if (step === 'confirm') return (
    <div className="step-card">
      <div className="step-head">
        <span className="step-title">Transaction Complete</span>
      </div>
      <ProgressBar step={step} />
      <div className="confirm-body">
        <div className="success-icon">✓</div>
        <div className="confirm-h">{mode === 'swap' ? 'Swap' : 'Bridge'} Successful</div>
        <div className="confirm-p">Confirmed in ~2.3 seconds · {new Date().toLocaleTimeString()}</div>
        <div className="confirm-table">
          <div className="ct-row"><span className="ct-k">Sold</span><span className="ct-v">{savedAmt} {fromToken?.sym} on {fromChain.name}</span></div>
          <div className="ct-row"><span className="ct-k">Received</span><span className="ct-v green">{savedRec} {toToken?.sym}{mode === 'bridge' ? ` on ${toChain.name}` : ''}</span></div>
          <div className="ct-row"><span className="ct-k">Via</span><span className="ct-v">{wallet?.icon} {wallet?.name}</span></div>
          <div className="ct-row"><span className="ct-k">Status</span><span className="ct-v green">✓ Confirmed (3/3 blocks)</span></div>
        </div>
        <div className="hash-box">Tx Hash<br /><span>{txHash}</span></div>
        <button className="action-btn purple" onClick={reset} style={{ width: '100%', margin: '0 0 8px', display: 'block' }}>New Transaction</button>
        <button className="action-btn outline" onClick={reset} style={{ width: '100%', margin: 0, display: 'block' }}>View on Explorer ↗</button>
      </div>
    </div>
  );
}
